
public class DoubleVarDemo3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int var;
		double x;
		var=10;
		x=10;
		System.out.println("var contains = " + var);
		System.out.println("x contains = " + x);

		//Now divide both by 4
		var=var/4;
		x= (int)x/4;
		System.out.println("var contains after division = " + var);
		System.out.println("x contains after division = " + x);

	}

}
